function adminicaMobile(){var e=$('<select class="full_width">').appendTo("#mobile_nav .main");var t=$('<select class="full_width">').appendTo("#mobile_nav .side");$("#nav_top li").each(function(){var t=$(this),n=t.find("> a"),r=n.children('span:not(".alert")').text(),i=t.parents("li"),s=(new Array(i.length+1)).join("-");if($(this).children("a").hasClass("hide_mobile")){}else if($(this).children("a").attr("href")=="#"){var o=$("<optgroup>").attr("label",s+" "+r).appendTo(e)}else{var o=$("<option>").text(s+" "+n.children('span:not(".alert")').text()).val(n.attr("href")).appendTo(e);if(t.hasClass("current")){o.attr("selected","selected")}}});$("#sidebar .side_accordion li").each(function(){var e=$(this),n=e.find("> a"),r=e.parents("li"),i=(new Array(r.length+1)).join("-");var s=$("<option>").text(i+" "+n.text()).val(n.attr("href")).appendTo(t);if(e.hasClass("current")){s.attr("selected","selected")}});$("#mobile_nav div select").change(function(){window.location=$(this).val()})}